setup.wedding.start = function(npcId: string): void {
  let out = "";
  ↂ.flag.marriage.discussion.inProgress = false;
  if (aw.npc[npcId] == null) {
aw.con.warn(`Error in setup.wedding.start, npc ${npcId} is not a valid npc. Please report this error!`);
  }
  switch (ↂ.flag.marriage.discussion.place) {
case "Town hall":
  out += setup.wedding.place.townHall;
  break;
case "Central Park":
  out += setup.wedding.place.centralPark;
  break;
case "Church of the Holy Phallus":
  out += setup.wedding.place.holyPhallus;
  break;
case "Church of Man":
  out += setup.wedding.place.churchOfMan;
  break;
case "Temple of Elder gods":
  out += setup.wedding.place.templeOfElderGods;
  break;
case "Farm coop":
  out += setup.wedding.place.farmCoop;
  break;
case "Rented hall":
  out += setup.wedding.place.rentedHall;
  break;
default:
  aw.con.warn(`setup.wedding.start found no correct wedding place in ↂ.flag.marriage.discussion.place (${ↂ.flag.marriage.discussion.place}). Using Town hall instead.`);
  out += setup.wedding.place.townHall;
  break;
  }
  out += `<p>${setup.wedding.guestsPC()}</p>`
  out += `<p><center><<button "Get prepared">><<scenego "Marriage-a">><</button>></center></p>`;
  const go = {
passage: "<<addtime 112>>",
content: out,
image: "IMG-Wedding-side",
topImage: "IMG-Wedding-top",
title: "Wedding",
allowSave: false,
sidebar: `<h2>${ↂ.flag.marriage.discussion.place}</h2>`,
showTime: true,
allowMenu: false,
  };
  setup.scenario.launch(go);
  ↂ.flag.marriage.attended = true;
  aw.S();
};

